/**
 * ----------------------------------------
 * animation rotate-center
 * ----------------------------------------
 */
@-webkit-keyframes rotate-center {
  0% {
    -webkit-transform: rotate(0);
    transform: rotate(0);
  }
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}
@keyframes rotate-center {
  0% {
    -webkit-transform: rotate(0);
    transform: rotate(0);
  }
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}

//Media rules
@media only screen and (max-width: 650px) {
  html {
    width: 100% !important;
    overflow-x: hidden !important;
  }

  body {
    background: $darkGreen;
    position: absolute;
    width: 100%;
    overflow-x: hidden !important;
  }

  .container {
    background-size: cover;
  }

  .container div {
    padding: 20px;
  }
  .top {
    height: 60vh;
    background: $blacktransparant;
  }

  .top h1 {
    @include bold-font();
    color: $light;
    padding-top: 0px;
    font-size: 50px;
  }

  .top p {
    @include normal-font();
    font-size: 14px;
  }

  .social-icons {
    font-size: 14px;
  }

  .middle {
    top: 60vh;
    margin-left: -20px;
  }

  .middle h2 {
    font-size: 16px;
  }

  .middle a {
    font-size: 16px;
  }

  .section .first-item {
    margin-top: 65px;
    width: 100%;
  }

  .section p {
    width: 100%;
  }

  .merch-section {
    margin-top: -28px;
  }

  .divider {
    margin-left: -20px;
  }

  .merch-grid {
    display: grid;
    grid-template-columns: 1fr;
    width: 80%;
  }

  .merch-grid-col {
    margin: 0px;
    border: 0px;
    background: $darkGreen;
    margin-left: -20px;
    margin-bottom: 20px;
    width: 90%;
  }

  .merch-grid-col p {
    text-align: center;
    color: $accent;
    font-size: 16px;
  }

  .digital-outlets {
    display: none;
  }

  .merch-img {
    width: 80%;
    border: 5px solid $light;
  }

  .footer p {
    @include normal-font();
    font-size: 14px;
    padding: 10px;
    border-top: 5px solid $light;
    text-align: center;
  }

  #music-player {
    display: none;
  }
}
